def readTransaction(w3, blockNum, txNum, contracts):
    tx = w3.eth.getTransactionFromBlock(blockNum, txNum)
    rcpt = w3.eth.getTransactionReceipt(tx.hash)

    print("Transaction: %d, %s, %f, %s" % (txNum, tx.hash.hex(), w3.fromWei(tx.value, 'ether'), rcpt['status']))

    input = tx['input']
    to = tx['to']
    if len(input) > 2:
        if to not in contracts:
            contracts[to] = decodeContractABI(to)
        if to in contracts and 'functions' in contracts[to] and input[0:10] in contracts[to]['functions']:
            print("Function: %s" % (contracts[to]['functions'][input[0:10]]))
        else:
            print("Function: %s" % (input[0:10]))
        input = input[10:]
        for j in range(len(input) // 64):
            print("Arg[%d]: %s" % (j + 1, input[j * 64: (j + 1) * 64]))

    for log in rcpt['logs']:
        print('Event address: ', log['address'])
        for i, topic in enumerate(log['topics']):
            print('Event topic %d: %s' % (i, topic.hex()))
        for i in range(len(log['data']) // 64):
            print("Event arg %d: %s" % (i, log['data'][i * 64: (i + 1) * 64]))

    return tx

